export interface EventsDestroyParams {
    /**
    * The id of the Direct Message event that should be deleted.
    */
    id: string;
}
export interface EventsShowParams {
    /**
    * The id of the Direct Message event that should be returned.
    */
    id: string;
}
export interface EventsNewParams {
    /**
    * Event object of the message event to send
    */
    event: {
        type: string;
        message_create: {
            target: {
                recipient_id: string;
            };
            message_data: {
                text: string;
                quick_reply?: {
                    type: string[];
                };
                attachment?: {
                    type: string;
                    media: {
                        id: string;
                    };
                };
            };
        };
    };
}
